    //         headers: {
    //             'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify({
    //             stock_name, email, password,
    //         }),

    //     })
    //     const data = await res.json()
    //     console.log(data)
    //     return data
    // }



    return (
        <Fragment>
            <h1 className="text-center font-bold ">My Stocks</h1>
            {stocks && stocks.length > 0 ? (
                <Table className="mt-4" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Subject</th>
                            <th>Name</th>
                            <th>Amount</th>
                            <th>Price</th>

                        </tr>
                    </thead>
                    <tbody>
                        {stocks &&
                            stocks.map((stock) => (
                                <tr key={stock._id}>
                                    <td>{stock.name}</td>
                                    <td>{stock.amount}</td>
                                    <td>{stock.price}</td>
                                    {/* <td style={{ width: "10rem" }}>
                                        {stock.date.substring(0, 10)}
                                    </td>
                                    <td>
                                        <Link to={`/edit/${stock._id}`}>
                                            <button className="btn btn-success">Edit</button>
                                        </Link>
                                    </td> */}

                                </tr>
                            ))}
                    </tbody>
                </Table>
            ) : (
                <Link to="/">
                    <Alert variant="warning" className="mt-4">
                        You currently do not have any stocks!
                    </Alert>
                </Link>
            )}
        </Fragment>


    )
}

export default List
