import React, { useEffect, useState } from 'react'
import Lists from './Lists'
import { Link } from "react-router-dom";
import axios from "axios";

const List = () => {
    const [stocks, setStocks] = useState([])
    useEffect(() => {
        const getStocks = async () => {
            const stocksFromServer = await fetchStocks()
            setStocks(stocksFromServer)
        }
        getStocks()
    }, [])

    const fetchStocks = async () => {
        const res = await fetch('localhost:27017/stock-data/stocks')
        const data = await res.json()
        console.log(data)
        return data
    }

    const addStocks = async (task) => {
        const res = await fetch('localhost:27017/stock-data/stocks', {
            method: 'POST',
            header: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify(task),
        })

        const data = await res.json()
        setStocks([...stocks, data])

    }



    return (
        <>
            (
            <Fragment>
                <h1 className="text-center mt-4">My Tickets</h1>
                {stocks && stocks.length > 0 ? (
                    <Table className="mt-4" striped bordered hover>
                        <thead>
                            <tr>
                                <th>Subject</th>
                                <th>Category</th>
                                <th>Priority</th>
                                <th>Description</th>
                                <th>Status</th>
                                <th>Date</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tickets &&
                                tickets.map((ticket) => (
                                    <tr key={ticket._id}>
                                        <td>{ticket.subject}</td>
                                        <td>{ticket.category}</td>
                                        <td>{ticket.priority}</td>
                                        <td>{ticket.description}</td>
                                        <td>{ticket.status}</td>
                                        <td style={{ width: "10rem" }}>
                                            {ticket.date.substring(0, 10)}
                                        </td>
                                        <td>
                                            <Link to={`/edit/${ticket._id}`}>
                                                <button className="btn btn-success">Edit</button>
                                            </Link>
                                        </td>
                                        <td>
                                            <button
                                                className="btn btn-danger"
                                                onClick={() => handleDelete(ticket._id)}
                                            >
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                        </tbody>
                    </Table>
                ) : (
                    <Link to="/profile">
                        <Alert variant="warning" className="mt-4">
                            You currently do not have any tickets!
                        </Alert>
                    </Link>
                )}
            </Fragment>
            );
};
        </>


    )
}

export default List
