import React, { useEffect, useState } from "react";
import axios from "axios";

const BuyList = ({ userId }) => {
    //defining useState
    const [profileItem, setProfileItem] = useState([]);
    let temp = profileItem;
    const [totalAmount, setTotalAmount] = useState(0);

    //@desc load data when ui loads
    useEffect(() => {
        if (localStorage.getItem("Profile")) {
            setProfileItem(JSON.parse(localStorage.getItem("Profile")));
        }
    }, [userId]);

    //@desc update state(Profile data) when local storage updates
    window.addEventListener("storage", () => {
        let localData = JSON.parse(localStorage.getItem("Profile"));
        setProfileItem(localData);
    });

    //@desc calculate totalAmount
    useEffect(() => {
        let tempAmount = 0;
        profileItem.map((item) => {
            tempAmount += item.stock_price * item.quantity;
        });
        setTotalAmount(tempAmount);
    }, [profileItem]);

    //@desc remove stocks from the Profile
    const handleRemoveFromProfile = (index) => {
        temp = profileItem;
        temp.splice(index, 1);
        setProfileItem(temp);
        localStorage.setItem("Profile", JSON.stringify(temp));
        //@desc create event of local storage
        window.dispatchEvent(new Event("storage"));
    };

    //@desc handle user checkout
    const handleCheckout = () => {
        if (userId !== undefined) {
            if (!profileItem.length) {
                window.alert("Profile is empty");
            } else {
                let success = 1;
                profileItem.map((item) => {
                    axios
                        .post("http://localhost:4600/order/add-order", {
                            userId: userId,
                            itemId: item.id,
                            itemName: item.name,
                            itemPrice: item.price,
                            itemQuantity: item.quantity,
                        })
                        .then((res) => {
                            if (success === profileItem.length) {
                                localStorage.removeItem("Profile");
                                setProfileItem([]);
                                temp = [];
                            } else {
                                console.log(success);
                                success++;
                            }
                        })
                        .catch((err) => {
                            console.log(err);
                        });
                });

                console.log("checked out");
            }
        } else {
            window.alert("you are not logged in");
        }
    };
    return (
        <div>
            <ul className="list-group pointer ">
                {/* Profile  */}
                <li
                    className="list-group-item text-center fw-bold fs-5 "
                    aria-current="true"
                >
                    <span className="heading"> Profile Item</span>
                </li>

                {/* @condition check if ProfileItem has item or not  
            @true display item in list format 
            @false notify user about empty Profile
        */}
                {profileItem.length ? (
                    profileItem.map((item, index) => (
                        <li
                            className="list-group-item d-flex justify-content-between align-items-start"
                            key={index}
                        >
                            <div className="ms-2 me-auto">
                                <div className="fw-bold">
                                    {index + 1}.{item.name}
                                </div>
                                <div> Price: {item.price}</div>
                                <div> Quantity: {item.quantity}</div>
                            </div>
                            <div>
                                <div className="text-end">{item.price * item.quantity}</div>

                                <button
                                    className="btn badge rounded-pill bg-danger"
                                    //@desc call handleRemoveFromProfile function
                                    onClick={(e) => {
                                        e.preventDefault();
                                        handleRemoveFromProfile(index);
                                    }}
                                >
                                    Remove
                                </button>
                            </div>
                        </li>
                    ))
                ) : (
                    <li
                        className="list-group-item text-center text-muted "
                        aria-current="true"
                    >
                        No Item Available
                    </li>
                )}
                <li className="list-group-item fw-bold " aria-current="true">
                    <div className="d-flex justify-content-between">
                        Total Amount
                        <span>{totalAmount}</span>
                    </div>
                </li>
                {/* @desc checkout button  */}
                <div className="d-grid gap-2 mt-2">
                    <button
                        className="btn btn-success"
                        onClick={(e) => {
                            e.preventDefault();
                            handleCheckout();
                        }}
                    >
                        Checkout
                    </button>
                </div>
            </ul>
        </div>
    );
};
