import React, { useEffect, useState, Fragment } from 'react';
import axios from "axios";

import { Link } from "react-router-dom";
import { Table, Alert } from "react-bootstrap";

const List = () => {
    const [stocks, setStocks] = useState([])
    // useEffect(() => {
    //     axios
    //         .get("http://localhost:1337/api/stocks")
    //         .then((res) => {

    //             setStocks(res.data);

    //             console.log(setStocks)

    //         })
    //         .catch((err) => {
    //             console.log(err.response);
    //         });
    // }, []);


    useEffect(() => {
        const getStocks = async () => {
            const tasksFromServer = await fetchtocks()
            settocks(tasksFromServer)
        }

        getTasks()

    }, [])
    const fetchStocks = async () => {
        const res = await fetch('http://localhost:3000/tasks')
        const data = await res.json()
        console.log(data)
        return data
    }



    return (
        <Fragment>
            <h1 className="text-center mt-4">My stocks</h1>
            {stocks && stocks.length > 0 ? (
                <Table className="mt-4" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Subject</th>
                            <th>Name</th>
                            <th>Amount</th>
                            <th>Price</th>

                        </tr>
                    </thead>
                    <tbody>
                        {stocks &&
                            stocks.map((stock) => (
                                <tr key={stock._id}>
                                    <td>{stock.name}</td>
                                    <td>{stock.amount}</td>
                                    <td>{stock.price}</td>
                                    <td style={{ width: "10rem" }}>
                                        {stock.date.substring(0, 10)}
                                    </td>
                                    <td>
                                        <Link to={`/edit/${stock._id}`}>
                                            <button className="btn btn-success">Edit</button>
                                        </Link>
                                    </td>

                                </tr>
                            ))}
                    </tbody>
                </Table>
            ) : (
                <Link to="/create">
                    <Alert variant="warning" className="mt-4">
                        You currently do not have any stocks!
                    </Alert>
                </Link>
            )}
        </Fragment>


    )
}

export default List
