import React, { useEffect, useState, useContext, Fragment } from 'react';
import axios from "axios";
import Lists from './Lists';
import { Link } from "react-router-dom";
import { Table, Alert } from "react-bootstrap";

const List = () => {
    const [stocks, setStocks] = useState([])
    useEffect(() => {
        const getStocks = async () => {
            const stocksFromServer = await fetchStocks()
            setStocks(stocksFromServer)
        }
        getStocks()
    }, [])

    const fetchStocks = async () => {
        const res = await fetch('localhost:27017/stock-data/stocks')
        const data = await res.json()
        console.log(data)
        return data
    }

    const addStocks = async (task) => {
        const res = await fetch('localhost:27017/stock-data/stocks', {
            method: 'POST',
            header: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify(task),
        })

        const data = await res.json()
        setStocks([...stocks, data])

    }



    return (
        <Fragment>
            <h1 className="text-center mt-4">My stocks</h1>
            {stocks && stocks.length > 0 ? (
                <Table className="mt-4" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Subject</th>
                            <th>Category</th>
                            <th>Priority</th>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Date</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stocks &&
                            stocks.map((stock) => (
                                <tr key={stock._id}>
                                    <td>{stock.subject}</td>
                                    <td>{stock.category}</td>
                                    <td>{stock.priority}</td>
                                    <td>{stock.description}</td>
                                    <td>{stock.status}</td>
                                    <td style={{ width: "10rem" }}>
                                        {stock.date.substring(0, 10)}
                                    </td>
                                    <td>
                                        <Link to={`/edit/${stock._id}`}>
                                            <button className="btn btn-success">Edit</button>
                                        </Link>
                                    </td>

                                </tr>
                            ))}
                    </tbody>
                </Table>
            ) : (
                <Link to="/create">
                    <Alert variant="warning" className="mt-4">
                        You currently do not have any stocks!
                    </Alert>
                </Link>
            )}
        </Fragment>


    )
}

export default List
